import React, { Component } from 'react'
import axios from 'axios'
import RecordNav from './components/Nav/RecordNav'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import { setCurrentUser } from './actions/authActions'
import SquatImg from './components/icon/squat-icon.png'

class RecordList extends Component {
  constructor(props) {
    super(props)
    this.state = { records: [] }
  }

  componentDidMount() {
    axios
      .get('/api/counts/' + this.props.auth.user.id)
      .then((response) => {
        this.setState({ records: response.data })
        // console.log(response.data)
        // console.log(this.state.records)
      })
      .catch((error) => {
        console.log(error)
      })

    const { user } = this.props.auth
    axios
      .get('api/users/' + user.id)
      .then((response) => {
        // console.log(response.data)
        this.setState({
          user: response.data,
          name: response.data.name,
          email: response.data.email,
          birthday: response.data.birthday,
          img: response.data.img,
        })
        console.log(this.state.user)
      })
      .catch(function (error) {
        console.log(error)
      })
  }

  recordList() {
    return this.state.records
      .sort((a, b) => (a.date < b.date ? 1 : -1))
      .map((currentrecord) => {
        return (
          <tr>
            <td>{currentrecord.event}</td>
            <td>{currentrecord.count}</td>
            <td>{currentrecord.date.substring(0, 10)}</td>
          </tr>
        )
      })
  }

  render() {
    return (
      <div>
        <RecordNav />
        <div className="container">
          <div className="row">
            <div className="col s6 m6 l6">
              <div className="card">
                <div style={{ marginTop: '2rem' }} className="row">
                  <div
                    className="col s8 offset-s2"
                    style={{
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <div
                      style={{
                        width: '30px',
                        height: '30px',
                        border: '3px solid #ff5722',
                        borderRadius: '50%',
                        cursor: 'pointer',
                        marginBottom: '20px',
                      }}
                    >
                      <img
                        src={SquatImg}
                        style={{
                          width: '100%',
                          height: '100%',
                          position: 'acsolute',
                          borderRadius: '50%',
                        }}
                        alt="profile img"
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="col s6 m6 l6">
              <div className="card">
                <div style={{ marginTop: '2rem' }} className="row">
                  <div
                    className="col s8 offset-s2"
                    style={{
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <div
                      style={{
                        width: '30px',
                        height: '30px',
                        border: '3px solid #ff5722',
                        borderRadius: '50%',
                        cursor: 'pointer',
                        marginBottom: '20px',
                      }}
                    >
                      <img
                        src={SquatImg}
                        style={{
                          width: '100%',
                          height: '100%',
                          position: 'acsolute',
                          borderRadius: '50%',
                        }}
                        alt="profile img"
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="col s6 m6 l6">
              <div className="card">
                <div style={{ marginTop: '2rem' }} className="row">
                  <div
                    className="col s8 offset-s2"
                    style={{
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <div
                      style={{
                        width: '30px',
                        height: '30px',
                        border: '3px solid #ff5722',
                        borderRadius: '50%',
                        cursor: 'pointer',
                        marginBottom: '20px',
                      }}
                    >
                      <img
                        src={SquatImg}
                        style={{
                          width: '100%',
                          height: '100%',
                          position: 'acsolute',
                          borderRadius: '50%',
                        }}
                        alt="profile img"
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="col s6 m6 l6">
              <div className="card">
                <div style={{ marginTop: '2rem' }} className="row">
                  <div
                    className="col s8 offset-s2"
                    style={{
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <div
                      style={{
                        width: '30px',
                        height: '30px',
                        border: '3px solid #ff5722',
                        borderRadius: '50%',
                        cursor: 'pointer',
                        marginBottom: '20px',
                      }}
                    >
                      <img
                        src={SquatImg}
                        style={{
                          width: '100%',
                          height: '100%',
                          position: 'acsolute',
                          borderRadius: '50%',
                        }}
                        alt="profile img"
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="col s6 m6 l6">
              <div className="card">
                <div style={{ marginTop: '2rem' }} className="row">
                  <div
                    className="col s8 offset-s2"
                    style={{
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <div
                      className="card-image"
                      style={{
                        width: '30px',
                        height: '30px',
                        border: '3px solid #ff5722',
                        borderRadius: '50%',
                        cursor: 'pointer',
                        marginBottom: '20px',
                      }}
                    >
                      <img
                        src={SquatImg}
                        style={{
                          width: '100%',
                          height: '100%',
                          position: 'acsolute',
                          borderRadius: '50%',
                        }}
                        alt="profile img"
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Daily Record */}
        <div className="container">
          <div className="card">
            <table
              className="table highlight centered"
              style={{ marginTop: '50px' }}
            >
              <thead>
                <tr className="red darken-1">
                  <th>Event</th>
                  <th>Count</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>{this.recordList()}</tbody>
            </table>
          </div>
        </div>
      </div>
    )
  }
}

RecordList.propTypes = {
  setCurrentUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
}

const mapStateToProps = (state) => ({
  auth: state.auth,
})

export default connect(mapStateToProps, { setCurrentUser })(RecordList)
