import React, { Component } from 'react'
import axios from 'axios'
import SquatNav from '../Nav/SquatNav'
import RankingMenu from './RankingMenu'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import { setCurrentUser } from '../../actions/authActions'

class SquatRanking extends Component {
  constructor(props) {
    super(props)
    this.state = { users: [] }
  }

  componentDidMount() {
    axios
      .get('/api/users/squat/ranking/')
      .then((response) => {
        this.setState({ users: response.data })
        // console.log(response.data)
        console.log(this.state.users[0].img)
      })
      .catch((error) => {
        console.log(error)
      })
  }

  // TopThree() {
  //   return this.state.users
  //     .sort((a, b) => (a.total_squat_count < b.total_squat_count ? 1 : -1))
  //     .map((user, i) => {
  //       return (
  //         <li className="collection-item">
  //           <div className="card">
  //             <div class="card-image waves-effect waves-block waves-light">
  //               <img
  //                 className="activator"
  //                 src={this.state.users[i].img}
  //                 alt="user img"
  //               />
  //             </div>
  //             <div class="card-content">
  //               <span class="card-title activator grey-text text-darken-4">
  //                 Card Title<i class="material-icons right">more_vert</i>
  //               </span>
  //             </div>
  //           </div>
  //         </li>
  //       )
  //     })
  // }

  rankList() {
    return this.state.users
      .sort((a, b) => (a.total_squat_count < b.total_squat_count ? 1 : -1))
      .map((user, i) => {
        return (
          <tr>
            <td style={{ fontSize: '30px' }}>{i + 1}</td>
            <td>
              <div
                className="col s12"
                style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                }}
              >
                <div
                  style={{
                    height: '50px',
                    width: '50px',
                    border: '1px solid #ff5722',
                    borderRadius: '50%',
                    cursor: 'pointer',
                  }}
                >
                  <img
                    src={user.img}
                    style={{
                      width: '100%',
                      height: '100%',
                      position: 'acsolute',
                      borderRadius: '50%',
                    }}
                    alt="profile img"
                  />
                </div>
              </div>
            </td>
            <td style={{ fontSize: '20px' }}>{user.name}</td>
            <td style={{ fontSize: '20px' }}>{user.total_squat_count}</td>
          </tr>
        )
      })
  }

  render() {
    return (
      <div>
        <SquatNav />
        <div className="card">
          <div class="card-image waves-effect waves-block waves-light">
            {this.state.users[0] && (
              <img
                className="activator"
                src={this.state.users[0].img}
                alt="user img"
              />
            )}
          </div>
          <div class="card-content">
            <span class="card-title activator grey-text text-darken-4">
              Card Title<i class="material-icons right">more_vert</i>
            </span>
          </div>
        </div>
        {/* <div className="container">
          <ul className="collection">{this.TopThree()}</ul>
        </div> */}
        <div className="container">
          <div className="white">
            <RankingMenu />
            <table className="table highlight centered">
              <thead>
                <tr className="white">
                  <th>Rank</th>
                  <th>Icon</th>
                  <th>Name</th>
                  <th>Count</th>
                </tr>
              </thead>
              <tbody>{this.rankList()}</tbody>
            </table>
          </div>
        </div>
      </div>
    )
  }
}

SquatRanking.propTypes = {
  setCurrentUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
}

const mapStateToProps = (state) => ({
  auth: state.auth,
})

export default connect(mapStateToProps, { setCurrentUser })(SquatRanking)
