import React, { Component } from 'react'
import axios from 'axios'
import Nav from '../Nav'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import { setCurrentUser } from '../../actions/authActions'

class Ranking extends Component {
  constructor(props) {
    super(props)
    this.state = { records: [] }
    this.state = { users: [] }
  }

  componentDidMount() {
    axios
      .get('/api/counts/squat/ranking')
      .then((response) => {
        this.setState({ records: response.data })
        const totalScoreList = this.state.records.map((record, i) => {
          if (this.state.records.[i].user == this.state.records.[i].user)
          return (
            <div>
              <div></div>
            </div>
          )
        })
        console.log(totalScoreList)
        // console.log(response.data)
      })
      .catch((error) => {
        console.log(error)
      })

    axios
      .get('/api/users')
      .then((response) => {
        this.setState({ users: response.data })
        // console.log(this.state.users)
      })
      .catch((error) => {
        console.log(error)
      })
  }

  totalCount() {
    return this.state.records
      .sort((a, b) => (a.date < b.date ? 1 : -1))
      .map((totalrecord) => {
        return (
          <tr>
            <td>{totalrecord.event}</td>
            <td>{totalrecord.count}</td>
            <td>{totalrecord.date.substring(0, 10)}</td>
          </tr>
        )
      })
  }

