import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import { setCurrentUser } from './actions/authActions'
import Nav from './components/Nav'

const ImgUpload = ({ onChange, src }) => {
  return (
    <label for="photo-upload" className="custom-file-upload fas">
      <div className="img-wrap img-upload">
        <img for="photo-upload" src={src} alt="profile img" />
      </div>
      <input id="photo-upload" type="file" onChange={onChange} />
    </label>
  )
}

const Name = ({ onChange, value }) => {
  return (
    <div className="input-field col s12">
      <input onChange={this.onChange} id="name" type="text" />
      <label htmlFor="name">
        <i className="material-icons left">person</i>Name
      </label>
    </div>
  )
}

const Email = ({ onChange, value }) => {
  return (
    <div className="input-field col s12">
      <input onChange={this.onChange} id="email" type="email" />
      <label htmlFor="email">
        <i className="material-icons left">mail</i>Email
      </label>
    </div>
  )
}

const Birthday = ({ onChange, value }) => {
  return (
    <div className="input-field col s12">
      <input onChange={this.onChange} id="birthday" type="text" />
      <label htmlFor="birthday">
        <i className="material-icons left">event_note</i>birthday
      </label>
    </div>
  )
}

const Profile = ({ onSubmit, src, name, status }) => {
  return (
    <div className="card">
      <form onSubmit={onSubmit}>
        <h1>Profile Card</h1>
        <label className="custom-file-upload fas">
          <div className="img-wrap">
            <img for="photo-upload" src={src} />
          </div>
        </label>
        <div className="name">{name}</div>
        <div className="status">{status}</div>
        <button type="submit" className="edit">
          Edit Profile{' '}
        </button>
      </form>
    </div>
  )
}

const Edit = ({ onSubmit, children }) => {
  return (
    <div className="card">
      <form onSubmit={onSubmit}>
        <h1>Profile Card</h1>
        {children}
        <button type="submit" className="save">
          Save{' '}
        </button>
      </form>
    </div>
  )
}

class CardProfile extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      file: '',
      imagePreviewUrl:
        'https://github.com/OlgaKoplik/CodePen/blob/master/profile.jpg?raw=true',
      name: '',
      status: '',
      active: 'edit',
    }
  }
  photoUpload(e) {
    e.preventDefault()
    const reader = new FileReader()
    const file = e.target.files[0]
    reader.onloadend = () => {
      this.setState({
        file: file,
        imagePreviewUrl: reader.result,
      })
    }
    reader.readAsDataURL(file)
  }
  editName(e) {
    const name = e.target.value
    this.setState({
      name,
    })
  }
  editStatus(e) {
    const status = e.target.value
    this.setState({
      status,
    })
  }
  handleSubmit(e) {
    e.preventDefault()
    let activeP = this.state.active === 'edit' ? 'profile' : 'edit'
    this.setState({
      active: activeP,
    })
  }

  render() {
    const { imagePreviewUrl, name, status, active } = this.state
    return (
      <div>
        {active === 'edit' ? (
          <Edit onSubmit={(e) => this.handleSubmit(e)}>
            <ImgUpload
              onChange={(e) => this.photoUpload(e)}
              src={imagePreviewUrl}
            />
            <Name onChange={(e) => this.editName(e)} value={name} />
            <Status onChange={(e) => this.editStatus(e)} value={status} />
          </Edit>
        ) : (
          <Profile
            onSubmit={(e) => this.handleSubmit(e)}
            src={imagePreviewUrl}
            name={name}
            status={status}
          />
        )}
      </div>
    )
  }
}

class UpdateProfile extends Component {
  render() {
    const { user } = this.props.auth
    console.log(user)
    return (
      <div>
        <Nav />
        <div className="container">
          <div className="row">
            <div className="col s8 offset-s2">
              <div className="col s12" style={{ paddingLeft: '11.250px' }}>
                <h4>
                  <b>Plofile Update</b>
                </h4>
              </div>
              <form noValidate onSubmit={this.onSubmit}>
                <div className="input-field col s12">
                  <input onChange={this.onChange} id="name" type="text" />
                  <label htmlFor="name">
                    <i className="material-icons left">person</i>Name
                  </label>
                </div>
                <div className="input-field col s12">
                  <input onChange={this.onChange} id="email" type="email" />
                  <label htmlFor="email">
                    <i className="material-icons left">mail</i>Email
                  </label>
                </div>
                <div className="input-field col s12">
                  <input onChange={this.onChange} id="birthday" type="text" />
                  <label htmlFor="birthday">
                    <i className="material-icons left">event_note</i>birthday
                  </label>
                </div>
                <div className="col s12" style={{ paddingLeft: '11.250px' }}>
                  <button
                    style={{
                      width: '150px',
                      borderRadius: '3px',
                      letterSpacing: '1.5px',
                      marginTop: '1rem',
                    }}
                    type="submit"
                    className="btn btn-large waves-effect waves-light hoverable deep-orange"
                  >
                    Uadate
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

UpdateProfile.propTypes = {
  setCurrentUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
}

const mapStateToProps = (state) => ({
  auth: state.auth,
})

export default connect(mapStateToProps, { setCurrentUser })(UpdateProfile)
