import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'

const Profile = (props) => (
  <tr>
    <td>{props.user.name}</td>
    <td>{props.user.email}</td>
    <td>
      <Link to={'/edit/' + props.user._id}>edit</Link> |
      <a
        href="/"
        onClick={() => {
          props.deleteProfile(props.user._id)
        }}
      >
        delete
      </a>
    </td>
  </tr>
)

export default class ProfileList extends Component {
  constructor(props) {
    super(props)

    this.deleteProfile = this.deleteProfile.bind(this)

    this.state = { users: [] }
  }

  componentDidMount() {
    axios
      .get('http://localhost:5000/users/')
      .then((response) => {
        this.setState({ users: response.data })
      })
      .catch((error) => {
        console.log(error)
      })
  }

  deleteProfile(id) {
    axios.delete('http://localhost:5000/users/' + id).then((response) => {
      console.log(response.data)
    })

    this.setState({
      exercises: this.state.users.filter((el) => el._id !== id),
    })
  }

  profileList() {
    return this.state.users.map((currentuser) => {
      return (
        <Profile
          exercise={currentuser}
          deleteExercise={this.deleteProfile}
          key={currentuser._id}
        />
      )
    })
  }

  render() {
    return (
      <div>
        <h3>Logged Exercises</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Username</th>
              <th>Description</th>
              <th>Duration</th>
              <th>Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>{this.profileList()}</tbody>
        </table>
      </div>
    )
  }
}
